import styles from "./Nav.module.css"
import {Link} from "react-router-dom"
import { useState } from "react"


function Nav ({items}){
    const [link, setLink] = useState("/")

    function getLink(item){
        setLink(`/${item}`.toLowerCase())

        return {link};
    }

    return (
        <ul className={styles.navList}>
            {
                items.map((item, key)=>{
                     return <li key={key} className={styles.navItem}> <Link to={getLink(item)}>{item}</Link></li>
                    //return <li key={key} className={styles.navItem}>{item}</li>
                })
            }
        </ul>
    )
}

export default Nav;
