                {   epiData && (
                        epiData.map((item, index)=>{                                    
                            return <CardItem key={index} dataKeys={dataKeys} data={epiData[index]}/>
                     }) 
                     )

                }                
            </div>
          </main>      
        </>
      );


}

export default Episodes;
