//CardItem index.js

import styles from "./CardItem.module.css" 
/* import CardInfo from "./../../components/CardInfo"
 */
function CardItem(props){
    //recibe un array (data keys) con los keys a a listar del json
    const {dataKeys} = props;    
    const {name, image} = props.data;    

    function showLyrics(e){
        const lyrics = e.target.attributes.lyrics.value;
        console.log(lyrics);
        return (
            <div width="600px" height="600px" background-color="red">
                <p>{lyrics}</p>
            </div> 
        )
    }

    return (
        <article className={styles.card}>
            <section className={styles.nameContainer}>
                <h2>{name}</h2>
            </section>
            <section className={styles.cap}>
                <h2>{name}</h2>
                <ul>
                    {dataKeys.map((item, key)=>{  
                        const value = props.data[item]
                        return <li key={key} className={styles.item}>
                                    { 
                                          item === 'link' ?                                     
                                            <a href={value} className={styles.itemKey} >+Info</a> 
                                        : item === 'lyrics' ?                                             
                                            <p className={styles.itemKey} lyrics={value} onClick={showLyrics}>Lyrics</p> 
                                        : <p className={styles.itemKey}>{item}:</p>
                                    }
                                    {
                                        item === "link" ? 
                                        <></>
                                        : item=== "lyrics" ?
                                            <p className={styles.itemLyric}>Sing Out Loud...</p>
                                        : <p className={styles.itemValue}>
                                            {value ? value : "No hay info"}
                                          </p>
                                    }

                               </li>
                    })}
                </ul>   
            </section>
            <section className={styles.imgContainer}>
                <img className={styles.cardImg} src={image} alt={name}/>
            </section>
        </article>  
    )
}
/*                                         <a href={value} className={styles.itemKey} onClick={showLyrics}>Lyrics</a>  */ 

export default CardItem;
