                {   charData && (
                        charData.map((item, index)=>{                                    
                            return <CardItem key={index} dataKeys={dataKeys} data=data={item}/>
                     }) 
                     )

                }                
            </div>
          </main>      
        </>
      ); */


}

export default Characters;
